package kr.co.scm.order.vo;

import kr.co.scm.base.vo.BaseVO;

public class ProductVO extends BaseVO{
	
	private String productCode;
	private String productName;
	private int productPrice;
	private int productQuantity;
	private String productUseable;
	private String productMemo;
	private String lprodCode;
	private String lprodName;
	
	private int orderProductNo;
	private int orderProductQuantity;
	
	
	public ProductVO() {
		// TODO Auto-generated constructor stub
	}
	
	public String getProductMemo() {
		return productMemo;
	}
	public void setProductMemo(String productMemo) {
		this.productMemo = productMemo;
	}
	
	public String getProductUseable() {
		return productUseable;
	}
	public void setProductUseable(String productUseable) {
		this.productUseable = productUseable;
	}
	public String getProductCode() {
		return productCode;
	}
	public void setProductCode(String productCode) {
		this.productCode = productCode;
	}
	public String getProductName() {
		return productName;
	}
	public void setProductName(String productName) {
		this.productName = productName;
	}
	
	public int getProductPrice() {
		return productPrice;
	}

	public void setProductPrice(int productPrice) {
		this.productPrice = productPrice;
	}

	public int getProductQuantity() {
		return productQuantity;
	}

	public void setProductQuantity(int productQuantity) {
		this.productQuantity = productQuantity;
	}

	public int getOrderProductNo() {
		return orderProductNo;
	}
	public void setOrderProductNo(int orderProductNo) {
		this.orderProductNo = orderProductNo;
	}
	
	
	public String getLprodCode() {
		return lprodCode;
	}

	public void setLprodCode(String lprodCode) {
		this.lprodCode = lprodCode;
	}

	public String getLprodName() {
		return lprodName;
	}

	public void setLprodName(String lprodName) {
		this.lprodName = lprodName;
	}

	public int getOrderProductQuantity() {
		return orderProductQuantity;
	}
	public void setOrderProductQuantity(int orderProductQuantity) {
		this.orderProductQuantity = orderProductQuantity;
	}
	
}
